  secretKey: string;
  serverName: string;
  region: string;
  cpuCores: number;
  ramGB: number;
  storageGB: number;
}

interface ServerStats {
  cpuUsage: number;
  ramUsage: number;
  diskUsage: number;
  uptime: string;
  status: 'online' | 'offline' | 'maintenance';
}

