  const [config, setConfig] = useState<VPSConfig>({
    dataUrl: 'https://api.minecraft-bots.com/v1/data/servers',
    secretKey: 'sk-mb_1234567890abcdef_your_minecraft_bot_api_key_here',
    serverName: 'MindzWeb-Bot-Server',
