#!/usr/bin/env tsx
import { botManager, BotConfig } from './botmineflayer';
import readline from 'readline';

// T·∫°o console interface
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

console.log(`
üå∏ ====================================== üå∏
     MINECRAFT BOT CONSOLE - LOLI CUTE
üå∏ ====================================== üå∏

Server: thuatzai123.aternos.me:38893
Phi√™n b·∫£n: 1.19.4

C√°c l·ªánh c√≥ s·∫µn:
  /start <t√™n_bot>  - T·∫°o v√† k·∫øt n·ªëi bot m·ªõi
  /stop <t√™n_bot>   - D·ª´ng bot
  /list             - Li·ªát k√™ t·∫•t c·∫£ bot ƒëang ch·∫°y  
  /say <t√™n_bot> <tin_nh·∫Øn> - G·ª≠i tin nh·∫Øn t·ª´ bot
  /status <t√™n_bot> - Xem tr·∫°ng th√°i bot
  /dance <t√™n_bot>  - Cho bot nh·∫£y m√∫a
  /help             - Hi·ªÉn th·ªã tr·ª£ gi√∫p
  /exit             - Tho√°t console

üå∏ ====================================== üå∏
`);

// Console callback ƒë·ªÉ nh·∫≠n log t·ª´ bot
const consoleCallback = (botId: string, level: string, message: string, source: string) => {
  const timestamp = new Date().toLocaleTimeString('vi-VN');
  const levelColor = {
    'info': '\x1b[36m',    // cyan
    'success': '\x1b[32m', // green  
    'warning': '\x1b[33m', // yellow
    'error': '\x1b[31m',   // red
    'debug': '\x1b[37m'    // white
  };
  const color = levelColor[level as keyof typeof levelColor] || '\x1b[37m';
  const reset = '\x1b[0m';

  console.log(`${color}[${timestamp}] [${botId}] ${level.toUpperCase()}: ${message}${reset}`);
};

// Map ƒë·ªÉ l∆∞u tr·ªØ c√°c bot instances
const activeBots = new Map<string, any>();

// Function ƒë·ªÉ x·ª≠ l√Ω c√°c l·ªánh
async function handleCommand(input: string) {
  const parts = input.trim().split(' ');
  const command = parts[0];

  try {
    switch (command) {
      case '/start':
        if (parts.length < 2) {
          console.log('‚ùå C√°ch s·ª≠ d·ª•ng: /start <t√™n_bot>');
          break;
        }
        const username = parts[1];

        if (activeBots.has(username)) {
          console.log(`‚ö†Ô∏è  Bot ${username} ƒë√£ ƒëang ch·∫°y r·ªìi!`);
          break;
        }

        console.log(`üöÄ ƒêang t·∫°o bot ${username}...`);
        const success = await botManager.connectBotToServer(username, username, consoleCallback);

        if (success) {
          const bot = botManager.getBot(username);
          if (bot) {
            activeBots.set(username, bot);
            console.log(`‚úÖ Bot ${username} ƒë√£ ƒë∆∞·ª£c t·∫°o th√†nh c√¥ng!`);
          }
        } else {
          console.log(`‚ùå Kh√¥ng th·ªÉ t·∫°o bot ${username}`);
        }
        break;

      case '/stop':
        if (parts.length < 2) {
          console.log('‚ùå C√°ch s·ª≠ d·ª•ng: /stop <t√™n_bot>');
          break;
        }
        const botToStop = parts[1];

        if (!activeBots.has(botToStop)) {
          console.log(`‚ö†Ô∏è  Bot ${botToStop} kh√¥ng t·ªìn t·∫°i ho·∫∑c ch∆∞a ch·∫°y!`);
          break;
        }

        console.log(`üîå ƒêang d·ª´ng bot ${botToStop}...`);
        botManager.removeBot(botToStop);
        activeBots.delete(botToStop);
        console.log(`‚úÖ Bot ${botToStop} ƒë√£ ƒë∆∞·ª£c d·ª´ng!`);
        break;

      case '/list':
        console.log('\nüìã DANH S√ÅCH BOT ƒêANG CH·∫†Y:');
        if (activeBots.size === 0) {
          console.log('   (Kh√¥ng c√≥ bot n√†o ƒëang ch·∫°y)');
        } else {
          activeBots.forEach((bot, name) => {
            const status = bot.getStatus();
            const statusIcon = status.connected ? 'üü¢' : 'üî¥';
            console.log(`   ${statusIcon} ${name} - ${status.connected ? 'Online' : 'Offline'} | HP: ${status.health} | Food: ${status.food} | Uptime: ${status.uptime}s`);
          });
        }
        console.log('');
        break;

      case '/say':
        if (parts.length < 3) {
          console.log('‚ùå C√°ch s·ª≠ d·ª•ng: /say <t√™n_bot> <tin_nh·∫Øn>');
          break;
        }
        const botToSpeak = parts[1];
        const message = parts.slice(2).join(' ');

        const speakBot = activeBots.get(botToSpeak);
        if (!speakBot) {
          console.log(`‚ö†Ô∏è  Bot ${botToSpeak} kh√¥ng t·ªìn t·∫°i ho·∫∑c ch∆∞a ch·∫°y!`);
          break;
        }

        speakBot.sendChat(message);
        console.log(`üí¨ Bot ${botToSpeak} ƒë√£ g·ª≠i: "${message}"`);
        break;

      case '/status':
        if (parts.length < 2) {
          console.log('‚ùå C√°ch s·ª≠ d·ª•ng: /status <t√™n_bot>');
          break;
        }
        const botToCheck = parts[1];

        const checkBot = activeBots.get(botToCheck);
        if (!checkBot) {
          console.log(`‚ö†Ô∏è  Bot ${botToCheck} kh√¥ng t·ªìn t·∫°i ho·∫∑c ch∆∞a ch·∫°y!`);
          break;
        }

        const status = checkBot.getStatus();
        const position = checkBot.getPosition();
        console.log(`
üìä TR·∫†NG TH√ÅI BOT: ${botToCheck}
üîó K·∫øt n·ªëi: ${status.connected ? '‚úÖ Online' : '‚ùå Offline'}
‚ù§Ô∏è  M√°u: ${status.health}/20
üçñ ƒê√≥i: ${status.food}/20  
‚è±Ô∏è  Uptime: ${status.uptime} gi√¢y
üìç V·ªã tr√≠: X:${position.x} Y:${position.y} Z:${position.z}
`);
        break;

      case '/dance':
        if (parts.length < 2) {
          console.log('‚ùå C√°ch s·ª≠ d·ª•ng: /dance <t√™n_bot>');
          break;
        }
        const botToDance = parts[1];

        const danceBot = activeBots.get(botToDance);
        if (!danceBot) {
          console.log(`‚ö†Ô∏è  Bot ${botToDance} kh√¥ng t·ªìn t·∫°i ho·∫∑c ch∆∞a ch·∫°y!`);
          break;
        }

        danceBot.sendChat('/dance');
        console.log(`üíÉ Bot ${botToDance} ƒëang b·∫Øt ƒë·∫ßu nh·∫£y m√∫a!`);
        break;

      case '/help':
        console.log(`
üå∏ ====== TR·ª¢ GI√öP L·ªÜNH BOT ====== üå∏

/start <t√™n>     - T·∫°o bot m·ªõi v·ªõi t√™n ch·ªâ ƒë·ªãnh
/stop <t√™n>      - D·ª´ng bot theo t√™n  
/list            - Xem t·∫•t c·∫£ bot ƒëang ch·∫°y
/say <t√™n> <msg> - G·ª≠i tin nh·∫Øn t·ª´ bot
/status <t√™n>    - Xem chi ti·∫øt tr·∫°ng th√°i bot
/dance <t√™n>     - Cho bot nh·∫£y m√∫a cute
/help            - Hi·ªÉn th·ªã tr·ª£ gi√∫p n√†y
/exit            - Tho√°t console

V√ç D·ª§:
  /start LoliBot1
  /say LoliBot1 Hello everyone!
  /dance LoliBot1
  /status LoliBot1
  /stop LoliBot1

üå∏ ============================== üå∏
`);
        break;

      case '/exit':
        console.log('üëã ƒêang d·ª´ng t·∫•t c·∫£ bot v√† tho√°t console...');

        // D·ª´ng t·∫•t c·∫£ bot tr∆∞·ªõc khi tho√°t
        activeBots.forEach((bot, name) => {
          console.log(`üîå D·ª´ng bot ${name}...`);
          botManager.removeBot(name);
        });

        console.log('‚úÖ ƒê√£ tho√°t console. Bye bye! üå∏');
        process.exit(0);
        break;

      default:
        if (input.trim() !== '') {
          console.log(`‚ùå L·ªánh kh√¥ng h·ª£p l·ªá: ${command}`);
          console.log('üí° G√µ /help ƒë·ªÉ xem danh s√°ch l·ªánh c√≥ s·∫µn.');
        }
        break;
    }
  } catch (error) {
    console.error(`‚ùå L·ªói khi th·ª±c hi·ªán l·ªánh: ${error}`);
  }
}

// Prompt ƒë·ªÉ nh·∫≠p l·ªánh
function prompt() {
  rl.question('üå∏ Bot Console > ', async (input) => {
    await handleCommand(input);
    prompt();
  });
}

// B·∫Øt ƒë·∫ßu console
console.log('üíï Bot Console ƒë√£ s·∫µn s√†ng! G√µ /help ƒë·ªÉ xem l·ªánh ho·∫∑c /start <t√™n_bot> ƒë·ªÉ b·∫Øt ƒë·∫ßu!');
prompt();

// X·ª≠ l√Ω tho√°t gracefully
process.on('SIGINT', () => {
  console.log('\nüëã ƒêang d·ª´ng t·∫•t c·∫£ bot...');
  activeBots.forEach((bot, name) => {
    botManager.removeBot(name);
  });
  console.log('‚úÖ ƒê√£ tho√°t! Bye bye! üå∏');
  process.exit(0);
});

