        const currentTime = Date.now()
        if (currentTime - lastMobCheckTime < 2000) return // Ch·ªâ check m·ªói 2 gi√¢y
        lastMobCheckTime = currentTime

        const food = bot.food

        // Ch·ªâ ki·ªÉm tra khi th·ª±c s·ª± c·∫ßn ƒÉn
        if (food >= 6) {
