  // Alias ƒë·ªÉ t∆∞∆°ng th√≠ch v·ªõi l·ªánh chat c≈©
  startSmartAutoFishing = function() {
    // D·ª´ng c√°c ho·∫°t ƒë·ªông kh√°c TR∆Ø·ªöC khi b·∫Øt ƒë·∫ßu c√¢u
    stopFollowing()
