        // B∆Ø·ªöC 2: Ch·ªâ c·∫ßm c·∫ßn c√¢u - b·ªè t·∫•t c·∫£ ƒë·ªì kh√°c
        if (!bot.heldItem || !bot.heldItem.name.includes('fishing_rod')) {
          await bot.equip(fishingRod, 'hand')
          console.log('üé£ Ch·ªâ c·∫ßm c·∫ßn c√¢u:', fishingRod.name)
