                setTimeout(findHook, 500) // Th·ª≠ l·∫°i sau 0.5 gi√¢y
              } else {
                console.log('‚ùå Kh√¥ng th·ªÉ t√¨m th·∫•y phao sau nhi·ªÅu l·∫ßn th·ª≠, th·∫£ c·∫ßn l·∫°i')
                isFishing = false
              }
            }
            findHook()
