        } else {
          bot.chat('ü•∫ Kh√¥ng t√¨m th·∫•y n∆∞·ªõc g·∫ßn! C·∫ßn t√¨m ao, s√¥ng ho·∫∑c bi·ªÉn~')
          stopSmartAutoFishing()
        }

      } catch (error) {
        console.log('‚ùå L·ªói smart fishing:', error)
        bot.chat('üòµ C√≥ l·ªói khi c√¢u c√°! Th·ª≠ l·∫°i sau~')
        isFishing = false
      }
    }, 6000) // Ki·ªÉm tra m·ªói 6 gi√¢y
  }

