    }

    if (!targetPlayer || !targetPlayer.position) {
      // Ch·ªâ log l·ªói m·ªói 10 gi√¢y (tƒÉng t·ª´ 5 gi√¢y) v√† reset found name
      if (lastFoundPlayerName !== '' || now - lastPlayerSearchLog > 10000) {
