    // Check if target is dead ho·∫∑c ƒëang respawn
    const isDead = targetPlayer.metadata?.[8] === 0 || targetPlayer.metadata?.[6] === 0
    const isRespawning = targetPlayer.position.y < 0 || !targetPlayer.isValid

    if (isDead || isRespawning) {
