    const distance = bot.entity.position.distanceTo(targetPlayer.position)

    // Check m√°u bot
    const health = bot.health

    // N·∫øu c√≥ t√°o v√†ng, ƒÉn ngay kh√¥ng c·∫ßn b·ªè ch·∫°y
    if (health < 10) {
