        } else {
          break
        }
      }

      bot.chat(`‚úÖ ƒê√£ t·∫•n c√¥ng ${targetMob}!`)
    } else {
      bot.chat(`ü•∫ Kh√¥ng t√¨m th·∫•y ${targetMob} g·∫ßn ƒë√¢y!`)
    }
  }

