import { Plus, Settings, Loader2, Check, AlertCircle } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";

interface BotConfig {
  botName: string;
  serverHost: string;
  serverPort: number;
  version: string;
}

export default function BotConfigForm() {
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [currentConfig, setCurrentConfig] = useState<BotConfig>({
    botName: 'botlolicute',
    serverHost: '',
    serverPort: 25565,
    version: '1.19.4'
  });
  const [formData, setFormData] = useState<BotConfig>({
    botName: 'botlolicute',
    serverHost: '',
    serverPort: 25565,
    version: '1.19.4'
  });
  const { toast } = useToast();

  // Load current config when component mounts
  useEffect(() => {
    loadCurrentConfig();
  }, []);

  const loadCurrentConfig = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/bot/config');
      const data = await response.json();

      if (data.success) {
        setCurrentConfig(data.config);
        setFormData(data.config);
      }
    } catch (error) {
      console.error('L·ªói t·∫£i c·∫•u h√¨nh:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsUpdating(true);
    setProgress(0);

    try {
      // Simulate progress update
      const progressInterval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return 90;
          }
          return prev + 10;
        });
      }, 400);

      const response = await fetch('/api/bot/config', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      // Complete progress
      setProgress(100);

      setTimeout(() => {
        if (data.success) {
          setCurrentConfig(formData);
          toast({
            title: "Th√†nh c√¥ng!",
            description: "C·∫•u h√¨nh bot ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t. Bot s·∫Ω t·ª± ƒë·ªông k·∫øt n·ªëi l·∫°i v·ªõi th√¥ng tin m·ªõi.",
          });
          setIsOpen(false);
        } else {
          toast({
            title: "L·ªói!",
            description: data.error || "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t c·∫•u h√¨nh bot",
            variant: "destructive",
          });
        }
        setIsUpdating(false);
        setProgress(0);
      }, 1000);

    } catch (error) {
      console.error('L·ªói c·∫≠p nh·∫≠t c·∫•u h√¨nh:', error);
      toast({
        title: "L·ªói k·∫øt n·ªëi!",
        description: "Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server",
        variant: "destructive",
      });
      setIsUpdating(false);
      setProgress(0);
    }
  };

  const handleInputChange = (field: keyof BotConfig, value: string | number) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button 
          className="bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white font-medium px-6 py-2 rounded-full shadow-lg transform transition-all hover:scale-105"
        >
          <Plus className="w-4 h-4 mr-2" />
          C·∫•u H√¨nh Bot
        </Button>
      </DialogTrigger>

      <DialogContent className="sm:max-w-[500px] bg-white/95 backdrop-blur-sm border-2 border-pink-200">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-purple-600 flex items-center">
            <Settings className="w-6 h-6 mr-2 text-pink-500" />
