#!/usr/bin/env node

import { spawn } from 'child_process';

console.log('üöÄ Starting web server and bot concurrently...');
console.log('üì± Web server: http://localhost:5000');
console.log('ü§ñ Bot: Minecraft bot manager');
console.log('‚îÄ'.repeat(50));

// T·∫°o m√†u s·∫Øc cho logs
const colors = {
  web: '\x1b[36m', // cyan
  bot: '\x1b[35m', // magenta
  reset: '\x1b[0m'
};

// Ch·∫°y web server
const webProcess = spawn('tsx', ['server/index.ts'], {
  env: { ...process.env, NODE_ENV: 'development' },
  stdio: 'pipe'
});

// Ch·∫°y bot
const botProcess = spawn('tsx', ['server/botlolicute.ts'], {
  env: { ...process.env, NODE_ENV: 'development' },
  stdio: 'pipe'
});

// X·ª≠ l√Ω output c·ªßa web server
webProcess.stdout.on('data', (data) => {
  process.stdout.write(`${colors.web}[üåêweb]${colors.reset} ${data}`);
});

webProcess.stderr.on('data', (data) => {
  process.stderr.write(`${colors.web}[üåêweb]${colors.reset} ${data}`);
});

// X·ª≠ l√Ω output c·ªßa bot
botProcess.stdout.on('data', (data) => {
  process.stdout.write(`${colors.bot}[ü§ñbot]${colors.reset} ${data}`);
});

botProcess.stderr.on('data', (data) => {
  process.stderr.write(`${colors.bot}[ü§ñbot]${colors.reset} ${data}`);
});

// X·ª≠ l√Ω khi process tho√°t
webProcess.on('close', (code) => {
  console.log(`${colors.web}[üåêweb]${colors.reset} Process exited with code ${code}`);
  botProcess.kill();
  process.exit(code);
});

botProcess.on('close', (code) => {
  console.log(`${colors.bot}[ü§ñbot]${colors.reset} Process exited with code ${code}`);
  if (code !== 0) {
    console.log('Bot process crashed, keeping web server running...');
  }
});

// X·ª≠ l√Ω t√≠n hi·ªáu d·ª´ng
process.on('SIGINT', () => {
  console.log('\nüõë Stopping both processes...');
  webProcess.kill();
  botProcess.kill();
  process.exit(0);
});

process.on('SIGTERM', () => {
  console.log('\nüõë Stopping both processes...');
  webProcess.kill();
  botProcess.kill();
  process.exit(0);
});

