import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { RefreshCw, Square } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Process } from "@shared/schema";

export default function ProcessManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get processes
  const { data: processes = [], isLoading, refetch } = useQuery<Process[]>({
    queryKey: ["/api/processes"],
    refetchInterval: false, // T·∫Øt auto-refresh ƒë·ªÉ tr√°nh spam
  });

  // Kill process mutation
  const killProcess = useMutation({
    mutationFn: async (pid: number) => {
      await apiRequest("DELETE", `/api/processes/${pid}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/processes"] });
      toast({
        title: "Success",
        description: "Process terminated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to terminate process",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "running":
        return "bg-green-500/20 text-green-400";
      case "sleeping":
        return "bg-blue-500/20 text-blue-400";
      case "stopped":
        return "bg-red-500/20 text-red-400";
      default:
        return "bg-gray-500/20 text-gray-400";
    }
  };

  const formatMemory = (kb: number) => {
    if (kb < 1024) return `${kb} KB`;
    return `${(kb / 1024).toFixed(1)} MB`;
  };

  const formatCpuUsage = (usage: number) => {
    return `${(usage / 100).toFixed(1)}%`;
  };

  if (isLoading) {
    return (
      <div className="flex-1 p-4 terminal-text">
        <div>Loading processes...</div>
      </div>
    );
  }

  return (
    <div className="flex-1 p-4">
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-lg font-semibold terminal-text">Process Manager</h2>
        <div className="flex items-center gap-2">
          <Button
            size="sm"
            onClick={() => refetch()}
            className="bg-green-500 text-black hover:bg-green-400"
          >
            <RefreshCw className="h-4 w-4 mr-1" />
            Refresh
          </Button>
          <Button
            size="sm"
            variant="destructive"
            className="bg-red-500 text-white hover:bg-red-400"
          >
            <Square className="h-4 w-4 mr-1" />
            Kill Process
          </Button>
        </div>
      </div>

      {/* Process Table */}
      <div className="terminal-panel rounded-lg border terminal-border overflow-hidden">
        {/* Table Header */}
        <div className="grid grid-cols-6 gap-4 p-3 border-b terminal-border bg-gray-600/30 text-xs font-semibold terminal-muted uppercase tracking-wide">
          <div>PID</div>
          <div>Process Name</div>
          <div>User</div>
          <div>CPU %</div>
          <div>Memory</div>
          <div>Status</div>
        </div>

        {/* Table Body */}
        <div className="max-h-96 overflow-y-auto custom-scrollbar">
          {processes.map((process) => (
            <div
              key={process.id}
              className="grid grid-cols-6 gap-4 p-3 border-b terminal-border/50 hover:bg-gray-600/20 text-sm font-mono group"
            >
              <div className="terminal-text">{process.pid}</div>
              <div className="terminal-text">{process.name}</div>
              <div className="terminal-muted">{process.user}</div>
              <div className={`${process.cpuUsage > 500 ? 'terminal-error' : process.cpuUsage > 200 ? 'terminal-warning' : 'terminal-success'}`}>
                {formatCpuUsage(process.cpuUsage)}
              </div>
              <div className="terminal-text">{formatMemory(process.memoryUsage)}</div>
              <div className="flex items-center justify-between">
                <Badge className={getStatusColor(process.status)}>
                  {process.status}
                </Badge>
                {process.pid !== 1 && ( // Don't allow killing init process
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => killProcess.mutate(process.pid)}
                    className="opacity-0 group-hover:opacity-100 transition-opacity p-1 text-red-400 hover:text-red-300"
                  >
                    <Square className="h-3 w-3" />
                  </Button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Process Stats */}
      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="terminal-panel border terminal-border rounded-lg p-4">
          <h3 className="text-sm font-semibold terminal-muted mb-2">Total Processes</h3>
          <div className="text-2xl font-bold terminal-text">{processes.length}</div>
        </div>
        <div className="terminal-panel border terminal-border rounded-lg p-4">
          <h3 className="text-sm font-semibold terminal-muted mb-2">Running</h3>
          <div className="text-2xl font-bold text-green-400">
            {processes.filter(p => p.status === "running").length}
          </div>
        </div>
        <div className="terminal-panel border terminal-border rounded-lg p-4">
          <h3 className="text-sm font-semibold terminal-muted mb-2">Average CPU</h3>
          <div className="text-2xl font-bold terminal-text">
            {processes.length > 0 
              ? formatCpuUsage(Math.round(processes.reduce((sum, p) => sum + p.cpuUsage, 0) / processes.length))
              : "0%"
            }
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/MusicPlayer.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Play, Pause, Volume2, VolumeX, RotateCcw } from "lucide-react";

export default function MusicPlayer() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [audioReady, setAudioReady] = useState(false);
  const audioRef = useRef<HTMLAudioElement>(null);

  // Generate a more pleasant melody programmatically
  const generateMelody = (): string => {
    try {
      const context = new (window.AudioContext || window.webkitAudioContext)();
      const sampleRate = context.sampleRate;
      const duration = 8; // 8 seconds
      const length = sampleRate * duration;
      const buffer = context.createBuffer(2, length, sampleRate);

      // Cute kawaii melody notes - higher pitched and more cheerful
      const notes = [523.25, 587.33, 659.25, 698.46, 783.99, 880.00, 987.77, 1046.50]; // C5, D5, E5, F5, G5, A5, B5, C6
      // Multiple cute melody patterns
      const melodyPatterns = [
        [0, 2, 4, 2, 6, 4, 2, 0, 1, 3, 5, 3, 7, 5, 3, 0], // Happy bounce
        [0, 4, 2, 6, 4, 0, 3, 5, 7, 5, 3, 1, 4, 2, 0], // Dreamy
        [2, 4, 6, 7, 6, 4, 2, 0, 3, 5, 7, 5, 3, 1, 2, 4], // Playful
        [0, 2, 4, 6, 7, 6, 4, 2, 5, 7, 5, 3, 1, 3, 5, 0], // Kawaii
      ];
      const melody = melodyPatterns[Math.floor(Math.random() * melodyPatterns.length)];

      for (let channel = 0; channel < 2; channel++) {
        const channelData = buffer.getChannelData(channel);

        for (let i = 0; i < length; i++) {
          const time = i / sampleRate;
          const noteIndex = Math.floor((time * 2) % melody.length);
          const frequency = notes[melody[noteIndex]];

          // Create a cute, bell-like tone (more kawaii)
          const fundamental = Math.sin(2 * Math.PI * frequency * time);
          const harmonic2 = Math.sin(2 * Math.PI * frequency * 2 * time) * 0.4;
          const harmonic3 = Math.sin(2 * Math.PI * frequency * 3 * time) * 0.2;
          const softening = Math.sin(2 * Math.PI * frequency * 0.5 * time) * 0.1; // Add softness

          let sample = (fundamental + harmonic2 + harmonic3 + softening) * 0.25;

          // Add cute bounce effect
          const noteTime = (time * 1.5) % 1;
          const envelope = noteTime < 0.05 ? noteTime * 20 : 
                          noteTime < 0.15 ? 1 :
                          noteTime > 0.85 ? (1 - noteTime) * 6.67 : 0.8;

          sample *= envelope;

          // Overall fade in/out
          const fadeTime = 0.5;
          if (time < fadeTime) {
            sample *= time / fadeTime;
          } else if (time > duration - fadeTime) {
            sample *= (duration - time) / fadeTime;
          }

          channelData[i] = sample;
        }
      }

      // Convert to WAV blob URL
      const wav = audioBufferToWav(buffer);
      return URL.createObjectURL(new Blob([wav], { type: 'audio/wav' }));
    } catch (error) {
      console.log("Could not generate melody:", error);
      // Fallback to a simple pleasant tone
      return "data:audio/wav;base64,UklGRnoGAABXQVZFZm10IBAAAAABAAEAQB8AAEAfAAABAAgAZGF0YQoGAACBhYqFbF1fdJivrJBhNjVgodDbq2EcBj+a2/LDciUFLIHO8tiJNwgZaLvt559NEAxQp+PwtmMcBjiR1/LMeSwFJHfH8N2QQAoUXrTp66hVFApGn+DyvGURCjeL0fPTgC4DJ27A7+OZURE=";
    }
  };

  const audioBufferToWav = (buffer: AudioBuffer): ArrayBuffer => {
    const length = buffer.length;
    const numberOfChannels = buffer.numberOfChannels;
    const sampleRate = buffer.sampleRate;
    const arrayBuffer = new ArrayBuffer(44 + length * numberOfChannels * 2);
    const view = new DataView(arrayBuffer);

    // WAV header
    const writeString = (offset: number, string: string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    writeString(0, 'RIFF');
    view.setUint32(4, 36 + length * numberOfChannels * 2, true);
    writeString(8, 'WAVE');
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, numberOfChannels, true);
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, sampleRate * numberOfChannels * 2, true);
    view.setUint16(32, numberOfChannels * 2, true);
    view.setUint16(34, 16, true);
    writeString(36, 'data');
    view.setUint32(40, length * numberOfChannels * 2, true);

    // Convert float samples to 16-bit PCM
    let offset = 44;
    for (let i = 0; i < length; i++) {
      for (let channel = 0; channel < numberOfChannels; channel++) {
        const sample = Math.max(-1, Math.min(1, buffer.getChannelData(channel)[i]));
        view.setInt16(offset, sample * 0x7FFF, true);
        offset += 2;
      }
    }

    return arrayBuffer;
  };

  const [audioSrc, setAudioSrc] = useState<string>("");

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = 0.15;

      // Generate cute melody
      const melody = generateMelody();
      setAudioSrc(melody);
      audioRef.current.src = melody;
      setAudioReady(true);
    }
  }, []);

  const togglePlayPause = () => {
    if (audioRef.current && audioReady) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play().catch((error) => {
          console.log("Audio play failed:", error);
        });
      }
      setIsPlaying(!isPlaying);
    }
  };

  const regenerateMusic = () => {
    if (audioRef.current) {
      const wasPlaying = isPlaying;
      audioRef.current.pause();
      setIsPlaying(false);
      setAudioReady(false);

      setTimeout(() => {
        const melody = generateMelody();
        setAudioSrc(melody);
        if (audioRef.current) {
          audioRef.current.src = melody;
          setAudioReady(true);
          if (wasPlaying) {
            audioRef.current.play().catch((error) => {
              console.log("Audio play failed:", error);
            });
          }
        }
      }, 100);
    }
  };

  const toggleMute = () => {
    if (audioRef.current) {
      audioRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <Card className="bg-white/80 backdrop-blur-sm max-w-md mx-auto shadow-lg">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-purple-600">üéµ Background Music</h3>
          <div className="flex space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={toggleMute}
              className="text-pink-500 hover:text-pink-600"
              title="B·∫≠t/t·∫Øt √¢m thanh"
            >
              {isMuted ? <VolumeX className="h-4 w-4" /> : <Volume2 className="h-4 w-4" />}
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={regenerateMusic}
              disabled={!audioReady}
              className="text-purple-500 hover:text-purple-600 disabled:text-gray-400"
              title="T·∫°o giai ƒëi·ªáu m·ªõi"
            >
              <RotateCcw className="h-4 w-4" />
            </Button>
            <Button
              onClick={togglePlayPause}
              disabled={!audioReady}
              className="bg-pink-500 hover:bg-pink-600 text-white disabled:bg-gray-400"
              size="sm"
            >
              {isPlaying ? <Pause className="mr-2 h-4 w-4" /> : <Play className="mr-2 h-4 w-4" />}
              {!audioReady ? "ƒêang t·∫°o..." : isPlaying ? "Pause" : "Play"}
            </Button>
          </div>
        </div>

        <div className="bg-pink-100 rounded-xl p-3">
          <p className="text-sm text-gray-600 mb-2">Now Playing:</p>
          <p className="font-medium text-purple-600">Cute Kawaii Melody üé∂</p>
          <p className="text-xs text-gray-500 mt-1">
            Nh·∫°c g·ªëc tham kh·∫£o: <a href="https://youtu.be/n_ZyXuSVQ5Q?si=5--FpbK0_698vPX6" 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-pink-500 hover:underline">
              Xem tr√™n YouTube
            </a>
          </p>
          <p className="text-xs text-pink-400 mt-1">üå∏ Nh·∫°c kawaii t·∫°o t·ª± ƒë·ªông - nh·∫•n n√∫t ‚Üª ƒë·ªÉ ƒë·ªïi giai ƒëi·ªáu</p>
          {!audioReady && (
            <p className="text-xs text-blue-400 mt-1">üîÑ ƒêang t·∫°o nh·∫°c kawaii cute...</p>
          )}
          {audioReady && !isPlaying && (
            <p className="text-xs text-green-400 mt-1">‚úÖ S·∫µn s√†ng ph√°t nh·∫°c! Nh·∫•n Play ƒë·ªÉ nghe</p>
          )}
          {isPlaying && (
            <p className="text-xs text-pink-500 mt-1">üéµ ƒêang ph√°t nh·∫°c kawaii cute v·ªõi giai ƒëi·ªáu ng·ªçt ng√†o...</p>
          )}
        </div>

        <audio
          ref={audioRef}
          loop
          preload="auto"
          onPlay={() => setIsPlaying(true)}
          onPause={() => setIsPlaying(false)}
          onEnded={() => setIsPlaying(false)}
          onError={(e) => {
            console.error("Audio error:", e);
            setAudioReady(false);
          }}
          onLoadedData={() => setAudioReady(true)}
          onCanPlay={() => setAudioReady(true)}
        >
          Your browser does not support the audio element.
        </audio>
      </CardContent>
    </Card>
  );
}

