    console.log(`‚ö†Ô∏è Port ${PORT} ƒë√£ ƒë∆∞·ª£c s·ª≠ d·ª•ng`);
  } else {
    console.log(`‚ùå L·ªói kh·ªüi ƒë·ªông prismarine-viewer:`, error.message);
  }
});

server.listen(PORT, '0.0.0.0', () => {
  console.log(`‚úÖ Bot Viewer Dashboard ƒë√£ kh·ªüi ƒë·ªông th√†nh c√¥ng!`);
  console.log(`üëÅÔ∏è Truy c·∫≠p dashboard t·∫°i: http://localhost:${PORT}`);
});

export default app;

